import React from "react";

export default function FlashCards(props){

    function flashAtive(){
        const cartao = document.getElementById('flashCards');
        cartao.style.backgroundColor=('Black')
    }
    function flashDesactive(){
        const cartao = document.getElementById('flashCards');
        cartao.style.backgroundColor=('Black')
    }


    return(
        <div id='flashCards' onMouseEnter={flashAtive} onMouseLeave={flashDesactive}>
            <h2>{props.titulo}</h2>
            <hr></hr>
            <p>{props.descricao}</p>
            {props.img}
        </div>
    )
}
